import { describe, it, expect, beforeEach, afterEach, vi } from 'vitest';
import { MIDIPlayer } from '@/features/landing/hero/piano/player/MIDIPlayer';

const TEST_MIDI_DATA_URI =
  'data:audio/midi;base64,';

describe('Player', (): void => {
  let player: MIDIPlayer;

  beforeEach((): void => {
    player = new MIDIPlayer();
    vi.useFakeTimers();
  });

  afterEach((): void => {
    if (player.isPlaying()) {
      player.stop();
    }
    vi.restoreAllMocks();
  });

  describe('loadDataUri', (): void => {
    it('should load data correctly from data uri', (): void => {
      player.loadDataUri(TEST_MIDI_DATA_URI);
      expect(player.buffer).not.toBeNull();
      expect(player.buffer?.length).toBeGreaterThan(0);
      expect(player.tracks.length).toBeGreaterThan(0);
    });
  });

  describe('getCurrentTick', (): void => {
    beforeEach((): void => {
      player.loadDataUri(TEST_MIDI_DATA_URI);
    });

    it('should return 0 after init', (): void => {
      expect(player.getCurrentTick()).toBe(0);
    });

    it('should return last known tick after pause', (): void => {
      player.skipToTick(1);
      player.play();
      vi.advanceTimersByTime(1000);
      player.pause();

      const currentTick: number = player.getCurrentTick();
      expect(currentTick).toBeGreaterThan(0);
    });

    it('should return 0 after stop', (): void => {
      player.skipToTick(1);
      player.play();
      vi.advanceTimersByTime(1000);
      player.stop();

      expect(player.getCurrentTick()).toBe(0);
    });

    it('should return correct tick when skipping without playing', (): void => {
      player.skipToTick(500);
      expect(player.getCurrentTick()).toBe(500);
    });
  });

  describe('getSongTimeRemaining', (): void => {
    beforeEach((): void => {
      player.loadDataUri(TEST_MIDI_DATA_URI);
    });

    it('should return totalTime after stop', (): void => {
      const totalTime: number = player.getSongTime();
      player.play();
      vi.advanceTimersByTime(1000);
      player.stop();

      const remaining: number = player.getSongTimeRemaining();
      expect(remaining).toBeGreaterThanOrEqual(totalTime - 1);
      expect(remaining).toBeLessThanOrEqual(totalTime + 1);
    });
  });
});
